package com.fiona.a20221120_myrabohuche_nycschools.presentation.adapter;import android.annotation.SuppressLint;import android.content.Context;import android.content.Intent;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.fiona.a20221120_myrabohuche_nycschools.R;import com.fiona.a20221120_myrabohuche_nycschools.SatActivity;import com.fiona.a20221120_myrabohuche_nycschools.data.models.School;import java.util.List;import androidx.annotation.NonNull;import androidx.cardview.widget.CardView;import androidx.recyclerview.widget.RecyclerView;public class NYCSchoolsAdapter extends RecyclerView.Adapter<NYCSchoolsAdapter.MyViewHolder> {    private Context context;    private List<School> itemDataList;    public NYCSchoolsAdapter(Context context, List<School> itemDataList) {        this.context = context;        this.itemDataList = itemDataList;    }    @NonNull    @Override    public MyViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {        View view= LayoutInflater.from(context).inflate(R.layout.nyc_school_single_item,viewGroup,false);        return new MyViewHolder(view);    }    @SuppressLint("RecyclerView")    @Override    public void onBindViewHolder(@NonNull MyViewHolder myViewHolder, final int i) {       String school_name=itemDataList.get(i).getSchool_name();       String dbn=itemDataList.get(i).getDbn();        myViewHolder.school_name.setText(school_name);        myViewHolder.card.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent ne=new Intent(context, SatActivity.class);                ne.putExtra("school_name",itemDataList.get(i).getSchool_name());                ne.putExtra("dbn",itemDataList.get(i).getDbn());                context.startActivity(ne);            }        });    }    @Override    public int getItemCount() {        if (itemDataList.size()<50){            return itemDataList.size();        }else        if (itemDataList.size()>0) {            return 50;        }        return itemDataList.size();    }    @Override    public long getItemId(int position) {        return position;    }    public class MyViewHolder extends RecyclerView.ViewHolder {        CardView card;        TextView school_name;        ImageView homelogo,awaylogo;        public MyViewHolder(@NonNull View itemView) {            super(itemView);            card=itemView.findViewById(R.id.cardview);            school_name=itemView.findViewById(R.id.schoolnameid);        }    }}